import Bottom from "./Bottom";
const text = 'Lorem Ipsum is simply dummy text of the printing and typesetting industry. Lorem Ipsum has been the industrys standard dummy text ever since the 1500s, when an unknown printer took a galley of type and scrambled it to make a type specimen book. It has survived not only five centuries, but also the leap into electronic typesetting, remaining essentially unchanged. It was popularised in the 1960s with the release of Letraset sheets containing Lorem Ipsum passages, and more recently with desktop publishing software like Aldus PageMaker including versions of Lorem Ipsum';


function func(props){
    if ((props.ind+1)===text.length) return <Bottom/>;
    return <div></div>

}
function Top() {
    var count = -1;
    const [c, setState] = useState(0);
    const [xx, setState2] = useState('0%');
    function highlight() {

        var value = document.getElementById("in").value;

  
            
            var letters = document.getElementById("pa");
            //console.log(letters);
            var i = 0;
            var txt='';     
            var tt=0;        
            for (i; i <= c; i++) {
                if (value[i] !== text[i]) {
                   txt += '<span style="color:red">' + text[i] + '</span>';
                }
                else{
                    txt += '<span style="color:blue">' + text[i] + '</span>';
                    tt++;


                }
            }

            txt+= '<span style="color:orange">' + text[c+1] + '</span>';    
            txt+=text.substr(c+2);
            letters.innerHTML=txt;
            setState2((tt/(c+1))*100  );


           
        setState(c + 1);



    }

    var display = {
        paddingTop: '20px',
        paddingBottom: '20px',
        paddingLeft: '10px',
        paddingRight: '10px',
        fontSize: '30px',
        color: 'rgba(0,0,0,0.3)'



    };
    return ( < div style = {display} >

        <div id='to'>
       <label id = 'pa' htmlFor ="in"  > Lorem Ipsum is simply dummy text of the printing and typesetting industry.Lorem Ipsum has been the industry 's standard dummy text ever since the 1500s, when an unknown printer took a 
        galley of type and scrambled it to make a type specimen book.It has survived not only five centuries, but also the leap into electronic typesetting, remaining essentially unchanged.It was popularised in the 1960 s with the release of
        Letraset sheets containing Lorem Ipsum passages, and more recently with desktop publishing software like Aldus PageMaker including versions of Lorem Ipsum. </label> 
       </div>
        <div id='bo'>
        <input type = "text"   id = "in"  onInput = { highlight } />
        <func
            ind = {c}
        />    
       </div>
        </div>
    );
}
export default Top;
