import React from "react";
const text =    '    Lorem Ipsum is simply dummy text of the printing and typesetting industry. Lorem Ipsum has been the industrys standard dummy text ever since the 1500s, when an unknown printer took a galley of type and scrambled it to make a type specimen book. It has survived not only five centuries, but also the leap into electronic typesetting, remaining essentially unchanged. It was popularised in the 1960s with the release of Letraset sheets containing Lorem Ipsum passages, and more recently with desktop publishing software like Aldus PageMaker including versions of Lorem Ipsum';
function highlight() {

    
    var value = document.getElementById("in").value; //The input value
  
    var startingIndex = text.indexOf(value); //The string index where the value begins in the paragraph
    
    if (startingIndex!==-1) { //If the value is within the text
    
      var endingIndex = startingIndex+value.length; //The string index where the value ends is just the length of the value added to the starting index
    
      var highlightedText = text.slice(0,startingIndex); //The text from the beginning to the start of the highlight
      highlightedText += "<span style=\"color:red;\">"; //Add the HTML which will cause the highlight
      highlightedText += text.slice(startingIndex,endingIndex); //Add the text to highlight
      highlightedText += "</span>"; //Add the HTML which will cause the end of the highlight
      highlightedText += text.slice(endingIndex); //Add the remaining text
      
      document.getElementById("para").innerHTML = highlightedText; //Set the HTML of the paragraph to be the new, highlighted string that we made.
      
    }
    
  }
function Top(){
    
    var display={
        paddingTop:'20px',
        paddingBottom:'20px',
        paddingLeft:'10px',
        paddingRight:'10px',
        fontSize:'30px',
        color:'rgba(0,0,0,0.3)'
        


    };
    return (
    <div style={display}>
     
       <p>Lorem Ipsum is simply dummy text of the
        printing and typesetting industry. Lorem Ipsum has been the 
        industry's standard dummy text ever since the 1500s, when an unknown printer took a 
        galley of type and scrambled it to make a type specimen book. It has survived not only five centuries, but also the 
        leap into electronic typesetting, remaining essentially unchanged. It was popularised in the 1960s with the release of
         Letraset sheets containing Lorem Ipsum passages, and more recently with desktop publishing software like Aldus PageMaker
          including versions of Lorem Ipsum.</p>
   
 
        
       <input type="text" id="in"  oninput="highlight()"/>
          
    </div>
    );
}
export default Top;
